Query:
let maxApps = 100;  
let maxUsers = 100;  
let maxNmids = 100;  
let relevantUrlEvents = materialize(EmailUrlInfo   
| where Url startswith "https://login.windows.net/common/oauth2" or Url startswith "https://login.microsoftonline.com/consumers/oauth2" or Url startswith "https://login.microsoftonline.com/common/oauth2"  
| where Url has "redirect_uri"   
| where Url has "client_id"  
| extend AppId = tostring(parse_url(Url)["Query Parameters"]["client_id"])  
| where isnotempty(AppId)  
| project EmailReceivedTime = Timestamp, ReportId, AppId, NetworkMessageId  
);  
//  
let affectedNmids = materialize(  
relevantUrlEvents  
| distinct NetworkMessageId  
| take maxNmids  
);  
//  
let hasAffectedNmids = isnotempty(toscalar(affectedNmids));  
//  
let relevantEmailEvents =   
EmailEvents  
| where hasAffectedNmids  
| where NetworkMessageId in (affectedNmids)  
| where isnotempty(RecipientObjectId)  
| where isnotempty(RecipientEmailAddress)  
| distinct RecipientObjectId, NetworkMessageId, RecipientEmailAddress  
| project-rename AccountObjectId = RecipientObjectId;  
//  
let affectedUsers = materialize(  
relevantEmailEvents  
| distinct AccountObjectId  
| take maxUsers  
);  
//  
let hasAffectedUsers = isnotempty(toscalar(affectedUsers));  
//  
let appIds = materialize(  
relevantUrlEvents  
| distinct AppId  
| take maxApps  
);  
//  
let hasAppId = isnotempty(toscalar(appIds));  
//  
let relevantCloudAppEvents =   
CloudAppEvents  
| where hasAppId  
| where hasAffectedUsers  
| where AccountObjectId in (affectedUsers)  
| where ActionType == "Consent to application."  
| where RawEventData has_any(appIds)  
| extend AppId = tostring(RawEventData.Target[4].ID)  
| where isnotempty(AppId)  
| where AppId in (appIds)  
| project ConsentTime=Timestamp, AppId, AppName = ObjectName, AccountObjectId;  
//  
let finalEmailEvents = materialize(  
relevantEmailEvents  
| join kind=inner relevantUrlEvents on NetworkMessageId  
| project EmailReceivedTime, ReportId, AccountObjectId, RecipientEmailAddress, AppId, NetworkMessageId  
);  
//  
relevantCloudAppEvents  
| join kind=inner finalEmailEvents on AppId, AccountObjectId  
| where EmailReceivedTime < ConsentTime  
| project Timestamp=EmailReceivedTime, ReportId, AccountObjectId, RecipientEmailAddress, AppId, AppName, NetworkMessageId
